# Define material types and their probabilities
materials = ['aluminium', 'gold', 'polypropylene', 'plastic']
probabilities = [0.4, 0.3, 0.2, 0.1]

# Function to generate a random material
def random_material():
    return np.random.choice(materials, p=probabilities)

