        spot_size = np.random.normal(3.3, 0.1)
        intensity = np.random.lognormal(np.log(6.83e19), np.sqrt(3.96e41/6.83e19**2))
        power = np.random.lognormal(np.log(8.45e12), np.sqrt(4.03e27/8.45e12**2))
    elif material == 'polypropylene':
        spot_size = np.random.normal(3.61, 0.1)
        intensity = np.random.lognormal(np.log(6.44e20), np.sqrt(3.96e41/6.44e20**2))
        power = np.random.lognormal(np.log(7.94e13), np.sqrt(4.03e27/7.94e13**2))
    else:
        spot_size = np.random.normal(3.61, 0.1)
        intensity = np.random.lognormal(np.log(6.22e20), np.sqrt(3.96e41/6.22e20**2))
        power = np.random.lognormal(np.log(7.68e13), np.sqrt(4.03e27/7.68e13**2))
    return spot_size, intensity, power

# Function to derive cutoff energy
def derive_cutoff_energy(material, energy):
    if material == 'aluminium':
        return np.random.normal(8.64, np.sqrt(156))
    elif material == 'gold':
        return np.random.uniform(4.0, 11.7)
    elif material == 'polypropylene':
        return np.random.normal(4.8, 2)
    else:
        return np.random.normal(8.64, np.sqrt(156))

# Generate synthetic data
