    pulse_width, energy = scale_pulse_width_and_energy(material)
    spot_size, intensity, power = control_spot_size_intensity_power(material, energy)
    cutoff_energy = derive_cutoff_energy(material, energy)
    synthetic_data.append([material, target_thickness, pulse_width, energy, spot_size, intensity, power, cutoff_energy])

print(synthetic_data)
